<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Karma Installer</title>
    <style>
        body, html {
            height: 100%;
            width: 100%;
            margin: 0;
            display: flex;
            justify-content: center;
            align-items: center;
            font-family: Arial, sans-serif;
            background-color: #494949;
            position: relative;
            overflow-x: hidden;
        }

        .overlay {
            position: absolute;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            background-color: black;
            z-index: 0;
            transition: opacity 2s ease;
        }

        video {
            position: absolute;
            width: 500px;
            height: auto;
            object-fit: cover;
            transition: opacity 2s ease;
            background-color: black;
            z-index: 1;
        }

        .container {
            text-align: center;
            padding: 20px;
            background-color: #0c0c0ca2;
            border-radius: 10px;
            box-shadow: 0 4px 10px rgba(0, 0, 0, 0.1);
            opacity: 0;
            visibility: hidden;
            transition: opacity 2s ease, visibility 2s ease;
            z-index: 2;
        }

        .processing {
            font-size: 18px;
            margin: 0;
            color: #ce0000c2;
            margin-bottom: 20px;
        }

        .container h1 {
            color: #007bff;
        }

        .detik {
            font-weight: bold;
            color: darkorange;
        }

        .processed {
            color: #28a745;
            font-weight: bold;
            font-size: 20px;
            transition: color 0.5s ease;
        }

        .notify {
            position: absolute;
            top: 10%;
            background-image: linear-gradient(to left, #000000, #ce0000c2, #007bff);
            background-size: 200% 100%;
            -webkit-background-clip: text;
            color: transparent;
            animation: colorChange 2.5s linear infinite;
            transition: color 1s ease-in-out;
            z-index: 1;
        }

        @keyframes colorChange {
            0% {
                background-position: 100% 0;
            }
            100% {
                background-position: -100% 0;
            }
        }

    </style>
</head>
<body>

    <h1 class="notify"> INSTALLING, TUNGGU... </h1>
    <div class="overlay" id="overlay"></div>

    <video id="video" autoplay muted loop>
        <source src="https://upload.wikimedia.org/wikipedia/commons/transcoded/9/99/Animation_of_Rotating_Earth_at_Night.webm/Animation_of_Rotating_Earth_at_Night.webm.720p.vp9.webm" type="video/webm">
    </video>
    
    <div class="container" id="content">
        <h1>Installation is completed</h1>
        <p id="notif" class="processing">Processing Admin Panel...</p>
        <span id="okeh" class="detik">20</span>
    </div>

    <script type="text/javascript">
        var K = 20;
        var L = document.getElementById('okeh');
        var X = document.getElementById('notif');
        var video = document.getElementById('video');
        var content = document.getElementById('content');
        var overlay = document.getElementById('overlay');
        var notify = document.querySelector('.notify');
        
        setTimeout(function() {
            video.style.opacity = '0';
            overlay.style.opacity = '0';
            notify.style.opacity = '0';
            content.style.opacity = '1';
            content.style.visibility = 'visible';
        }, 10000);

        var M = setInterval(function() {
            K--;
            L.innerHTML = K;
            if (K <= 0) {
                clearInterval(M);
                X.innerHTML = "ADMIN PANEL PROCESSED";
                X.classList.add("processed");
                L.innerHTML = "âœ”";
                L.classList.add("processed");
                setTimeout(function() {
                    window.location.href = "http://localhost/karma.php";
                }, 3500);
            }
        }, 1000);
    </script>

</body>
</html>